// import _ from 'lodash'
import { ADD_CART } from '../actions/cart'
const initial = {}

export default function (state = initial, action) {
  switch (action.type) {
    case ADD_CART:
      console.log({ [action.id]: action.colors })
      return { [action.id]: { quantity: action.quantity, color: action.colors } }

    default:
      return state
  }
}
