import React, { Component } from 'react'
import { Field, reduxForm } from 'redux-form'

class PostsNew extends Component {
  renderField (field) {
  	return (
      <div>
      <label>{field.label}</label>
      <input
          className='form-control'
          type='text'
          {...field.input}
  			/>
    </div>
  	)
  }

  // Dry up your code once you seetwo pieces of JSX looking similar
  // Dont duplicate logic, you can pass arbitrry properties in the component
  // and they would be passed to the field props.. it can be any name you choose

  render () {
    return (
      <div>
        <h2>Hello, create a new post here</h2>
        <form>
          <Field
            label='Name'
            name='name'
            component={this.renderField}
    		/>
          <Field
            label='Heading'
            name='heading'
            component={this.renderField}
    		/>
          <Field
            label='Post Content'
            name='content'
            component={this.renderField}
    		/>
        </form>
      </div>
    )
  }
}

export default reduxForm({
  form: 'PostsNewForm'
})(PostsNew)
