import React,{Component} from 'react'
import {connect} from 'react-redux'
import {fetchposts} from '../actions/notes'
import _ from 'lodash'

class Search extends Component{

	onInputChange(term){
		_.debounce(this.props.fetchposts(), 3000)
		console.log(true)
	}


	render(){
		return (
			<form className='form-inline my-2 my-md-0'>
                <input onChange={(e)=>{this.onInputChange(e.target.value)}} className='form-control' type='text' placeholder='Search' aria-label='Search' />
              </form>
		)
	}
}

function mapStateToProps(state){
	console.log(state)
	return state
}

export default connect(mapStateToProps, { fetchposts})(Search)
