import { LOGIN, LOGOUT, SIGNUP, CHECK, LOGIN_ERROR, LOGIN_SUCCESSFUL } from '../actions/auth'
const initial = {
  token: localStorage.getItem('token'),
  isAuthenticated: null,
  isLoading: true,
  user: null,
  errors: {}
}

export default function (state = initial, action) {
  switch (action.type) {
  	case LOGIN:
  		localStorage.setItem('token', action.payload.token)
  		initial.user = action.payload.user.username
  		console.log(initial)
  		console.log(action.payload)
  		return { ...initial, isAuthenticated: true, ...action.payload }

  	case LOGIN_ERROR:
  		console.log(action.payload)
  		return { ...initial, isAuthenticated: false, errors: action.payload }

    case CHECK:
    	if (action.payload == initial.token) {
    		return { ...initial, isAuthenticated: true }
    	}
    case LOGOUT:
    	if (initial.token) {
    		localStorage.removeItem('token')
    	initial.token = null
    	initial.user = null
    	console.log(initial)
      } else { console.log('EWOOO!!!') }

    	return { ...initial, isAuthenticated: null }
    default:
      return state
  }
}

