import _ from 'lodash'
import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { fetchposts } from '../actions/notes'
import PostRender from './PostRender'

class PostList extends Component {
  componentDidMount () {
    console.log(this.props.fetchposts())
    this.props.fetchposts()
  }

  navigateright () {
    this.props.fetchposts(this.props.posts.next)
  }
  navigateleft () {
    this.props.fetchposts(this.props.posts.previous)
  }

  render () {
    console.log(this.props)
    const { posts } = this.props
    if (!posts) {
      return (<h1>Loading.......</h1>)
    }

    return (
      <div className='container text-center'>
        <h3>Posts</h3>
        <PostRender post={this.props.posts} link={this.props.history} />
        { posts.previous !== null ? <button className='btn btn-default mx-2 my-4' onClick={this.navigateleft.bind(this)}>Previous</button>
          : ''}
        { posts.next !== null ? <button className='btn btn-primary mx-2 my-4' onClick={this.navigateright.bind(this)}>Next</button>
          : ''}

        <Link to='/posts/new'>Create Post</Link>

      </div>
    )
  }
}

function mapStateToProps (state) {
  return { posts: state.posts }
}

export default connect(mapStateToProps, { fetchposts })(PostList)
