import React, { Component } from 'react'

import CustomersService from '../actions/index'

const customersService = new CustomersService()
const URL = [
  {
    'pk': 1,
    'first_name': 'Customer 001',
    'last_name': 'Customer 001',
    'email': 'customer001@email.com',
    'phone': '00000000',
    'address': 'Customer 000 Address',
    'description': 'Customer 001 description'
  },
  {
    'pk': 2,
    'first_name': 'Ibaakee',
    'last_name': 'Ledum',
    'email': 'ledumibaakee@gmail.com',
    'phone': '09029632002',
    'address': 'Alakahi',
    'description': 'I have a lot to offer to my community'
  },
  {
    'pk': 3,
    'first_name': 'Chris',
    'last_name': 'Bologna',
    'email': 'bologna@code.com',
    'phone': '09029632002',
    'address': 'Alakahia,Uniport',
    'description': 'I am a coder'
  }

]

class CustomersList extends Component {
  constructor (props) {
    super(props)
    this.state = {
      customers: [],
      nextPageURL: '',
      URL

    }
    this.nextPage = this.nextPage.bind(this)
    this.handleDelete = this.handleDelete.bind(this)
  }

  componentDidMount () {
    var self = this
    customersService.getCustomers().then(function (result) {
    	// console.log(result)
      // self.setState({ customers: result.data, nextPageURL: result.nextlink })
      // const check = Array.isArray(self.state.customers)
      // console.log(check)
    })
  }

  handleDelete (e, pk) {
    var self = this
    customersService.deleteCustomer({ pk: pk }).then(() => {
      var newArr = self.state.customers.filter(function (obj) {
        return obj.pk !== pk
      })
      self.setState({ customers: newArr })
    })
  }

  nextPage () {
    var self = this
    customersService.getCustomersByUrl(this.state.nextPageURL)
      .then((result) => {
        self.setState({ customers: result.data, nextPageURL: result.nextlink })
      })
  }

  render () {
  	const pages = this.state.URL
  	console.log(pages)
    return (
      <div className='customers--list'>
        <table className='table'>
          <thead key='thead'>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>Address</th>
              <th>Description</th>
              <th>Actions</th>

            </tr>

          </thead>

          <tbody>
            {pages.map(c =>
              <tr key={c.pk}>
                <td>{c.pk}</td>
                <td>{c.first_name}</td>
                <td>{c.last_name}</td>
                <td>{c.phone}</td>
                <td>{c.email}</td>
                <td>{c.address}</td>
                <td>{c.description}</td>
                <td>
                  <button onClick={(e) => this.handleDelete(e, c.pk)}>
						Delete
                  </button>
                  <a href={'/customer' + c.pk}>Update</a>
                </td>
              </tr>
            )}
          </tbody>

        </table>

        <button className='btn btn-primary' onClick={this.nextPage}>
				Next
        </button>

      </div>

    )
  }
}

export default CustomersList
