import React, { Component } from 'react'
import { Field, reduxForm } from 'redux-form'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { createpost } from '../actions/notes'
import { FaBitcoin,FaPaperPlane,FaHome, FaPython } from 'react-icons/fa'
import classNames from 'classnames'
import Dropzone from 'react-dropzone'

const thumbsContainer = {
  display: 'flex',
  flexDirection: 'row',
  flexWrap: 'wrap',
  marginTop: 16
};

const thumb = {
  display: 'inline-flex',
  borderRadius: 2,
  border: '1px solid #eaeaea',
  marginBottom: 8,
  marginRight: 8,
  width: 100,
  height: 100,
  padding: 4,
  boxSizing: 'border-box'
};

const thumbInner = {
  display: 'flex',
  minWidth: 0,
  overflow: 'hidden'
}

const img = {
  display: 'block',
  width: 'auto',
  height: '100%'
};

const FILE_FIELD_NAME = 'files'

const renderDropzoneInput = (field) => {
  const files = field.input.value;
  return (
    <div>
      <Dropzone
        name={field.name}
        onDrop={( filesToUpload, e ) => field.input.onChange(filesToUpload)}
      >
        <div>Try dropping some files here, or click to select files to upload.</div>
      </Dropzone>
      {field.meta.touched &&
        field.meta.error &&
        <span className="error">{field.meta.error}</span>}
      {files && Array.isArray(files) && (
        <ul>
          { files.map((file, i) => <li key={i}>{file.name}</li>) }
        </ul>
      )}
    </div>
  );
}


class PostsNew extends Component {

  constructor(){
    super()
    this.state = {
      files:[]
    }
  }

  renderField (field) {
    const { meta: { touched, error } } = field
    const className = `form-group ${touched && error ? 'has-danger'
      : touched && !error ? 'has-none' : ''}`

    // field.meta.error is an automatic property added to field object
    // from validate function
    return (
      <div className={className}>
        <label>{field.label}</label>
        <input
          className='form-control rounded-0'
          type={`${field.type}`}
          {...field.input}
        />
        <div className='text-danger'>
          <small>{touched ? error : ''}</small>
        </div>
      </div>
    )
  }




  // different states of your form
  // a. pristine( no  input yet)
  // b. touched()
  // c. invalid

  // Dry up your code once you seetwo pieces of JSX looking similar
  // Dont duplicate logic, you can pass arbitrry properties in the component
  // and they would be passed to the field props.. it can be any name you choose

  onSubmit (values) {
    this.props.createpost(values, () => {
      this.props.history.push('/posts/')
    })
    this.props.reset()
  }

  onDrop = (acceptedFiles, rejectedFiles) =>{
    this.setState({files:files.map(file=>Object.assign(file, {
      preview: URL.createObjectURL(file)
    }))})
  }

  onCancel(){
    this.setState({files:[]})
  }

  handleChange = (event) =>{
    console.log('changed!!!!')
  }

  UploadField(field){

    console.log(field)
    // field.meta.error is an automatic property added to field object
    // from validate function
    return (
      <div class="custom-file">
    <input type={`${field.type}`} {...field.input} class="custom-file-input" value={null} id="validatedCustomFile" required/>
    <label class="custom-file-label" for="validatedCustomFile">Choose file...</label>
    <div class="invalid-feedback">Example invalid custom file feedback</div>
  </div>
    )
  }

  render () {
    // In using redux form the submit function is not really handled
    // by redux-form so we have to involve redux-form in the submittal
    //
    const { handleSubmit } = this.props
    // dis is pulled from the form helper that is included in content
    console.log(this.props)
    return (
      <div className='login'>
        <div className='container text-center pt-5'>
          <h2 className='text-white'>Hello, create a new post here</h2>
          <div className='row pt-5 justify-content-center'>
            <form className='col-sm-5 bg-dark text-white text-center rounded col-md-6 p-3' onSubmit={handleSubmit(this.onSubmit.bind(this))}>
            <p className='python'>Python
          <span className='ml-2'>
            <FaPython /></span></p>
              <Field
                label='Name'
                name='name'
                type='text'
                component={this.renderField}
              />
              <Field
                label='Heading'
                name='heading'
                type='text'
                component={this.renderField}
              />
              <Field
                label='Description'
                name='description'
                type='text'
                component={this.renderField}
              />
              <Dropzone onDrop={this.onDrop}>
        {({getRootProps, getInputProps, isDragActive}) => {
          console.log(getInputProps)
          console.log(getRootProps)
          const files = this.state.files.map(file => {
            return (
              <div style={thumb} key={file.name}>
              <div style={thumbInner}>
            <img 
            src={file.preview}
            style={img}/>
            </div></div>)
          })
          return (
            <div
              {...getRootProps({
                onClick:  event => console.log(event)
              })}
              className={classNames('dropzone', {'dropzone--isActive': isDragActive})}
            >
              <input {...getInputProps()} />
              {
                isDragActive ?
                  <p>Drop files here...</p> :
                  <p>Try dropping some files here, or click to select files to upload.</p>
              }
            <ul>{files}</ul>
            </div>
          )
        }}
      </Dropzone>

              <button type='submit' className='btn btn-primary rounded-0 my-3 mx-2'>
              Submit
              <span className='ml-2 float-right'>
              <FaPaperPlane/></span></button>

              <Link to='/posts' className='btn btn-success rounded-0 mx-2 my-3'>Cancel<span className='ml-2 float-right'>
              <FaHome/></span></Link>
            </form>
          </div></div></div>
    )
  }
}

// define a helper function to validate the form and pass it to redux
// form helper
// name property must be identical to show up in errors object
function validate (values) {
  // inorder to validate input return a object we create from validate
  // function....
  const errors = {}
  // Validate the input from 'values'

  if (!values.name) {
    errors.name = 'Enter a name'
  }

  if (!values.heading) {
    errors.heading = 'Enter a heading'
  }

  if (!values.description) {
    errors.description = 'Enter a Description'
  }

  // if (!values.agree || values.agree == false) {
  //  errors.agree = 'Hello'
  // }

  // if errors is empty the form is ready to submit,
  // else there is a problem and redux form displays errors object
  return errors
}

export default reduxForm({
  validate, // converted to ES6 syntax (validate: validate)
  form: 'PostsNewForm'
})(connect(null, { createpost })(PostsNew))
