import _ from 'lodash'
import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { fetchposts, getCustomersByUrl } from '../actions/notes'

class PostList extends Component {
  constructor (props) {
    super(props)
    this.state = {
      doneLoading: false
    }
  }
  componentDidMount () {
    this.props.fetchposts().then(
      this.setState({ doneLoading: true }))
    console.log(this.state)
  }

  nextPage () {
    this.props.getCustomersByUrl(this.props.posts.next)
  }

  previousPage () {
    this.props.getCustomersByUrl(this.props.posts.previous)
  }
  navigateright () {
    this.props.fetchposts(this.props.posts.next)
  }
  navigateleft () {
    this.props.fetchposts(this.props.posts.previous)
  }

  renderPosts () {
    console.log(this.props.posts.results)
    return _.map(this.props.posts.results, post => {
      return (
        <div>
          <li key={post.pk}>
            {post.name}
          </li>
        </div>
      )
    })
  }

  render () {
    console.log(this.props)
    const { posts } = this.props
    return (
      <div>
        <h3>Posts</h3>
        <ul>
          {this.renderPosts()}
        </ul>
        { this.props.posts.previous !== null ? <button className='btn btn-default' onClick={this.navigateleft.bind(this)}>Previous</button>
          : ''}
        { this.props.posts.next !== null ? <button className='btn btn-primary' onClick={this.navigateright.bind(this)}>Next</button>
          : ''}

        <Link to='/posts/new'>Create Post</Link>

      </div>
    )
  }
}

function mapStateToProps (state) {
  return { posts: state.posts }
}

export default connect(mapStateToProps, { fetchposts })(PostList)
