import React, { Component } from 'react'
import { connect } from 'react-redux'
import { fetchpost, deletePost } from '../actions/notes'
import { Link } from 'react-router-dom'
import logo from '../logo.svg'

class PostShow extends Component {
  componentDidMount () {
  	const { pk } = this.props.match.params
  	this.props.fetchpost(pk)
  }

  onDeleteClick () {
  	const { pk } = this.props.match.params
  	const { history } = this.props
  	this.props.deletePost(pk, () => {
  		history.push('/posts')
  	})
  }
  render () {
  	console.log(this.props)
  	const { post } = this.props
  	console.log(post)
  	if (!post) {
  		return (<h3>Loading....</h3>)
  	}

  	return (
    <div>
        <button
        className='btn btn-danger pull-xs-right'
        onClick={this.onDeleteClick.bind(this)}>
    		Delete Post
      </button>
        <button
        className='btn btn-info pull-xs-right'
        onClick={this.onDeleteClick.bind(this)}>
    		Edit Post
      </button>
        <h3>{post.name}</h3>
        <p>{post.description}</p>
        <Link className='btn btn-success my-3' to='/'>Home</Link>
      </div>
    )
  	}
}

// ownprops

function mapStateToProps ({ posts }, ownProps) {
  return { post: posts[ownProps.match.params.pk] }
}
export default connect(mapStateToProps, { fetchpost, deletePost })(PostShow)
