import _ from 'lodash'
import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { fetchposts } from '../actions/notes'

class PostList extends Component {
  constructor (props) {
  	this.state = {
  		next: null,
  		previous: null
  	}
  }

  componentDidMount () {
  	this.props.fetchposts().then((posts) => {
  		this.setState({
  		next: posts.next,
  		previous: posts.previous
  	})
  	})
  	console.log(this.state)
  }

  nextPage () {
  	const { next } = this.props.match.params
  	console.log(next)
  }

  renderPosts () {
  	console.log(this.props.posts.count)
  	return _.map(this.props.posts.results, post => {
  		return (
        <li key={post.pk}>
      {post.name}
    </li>
      )
  	})
  }

  render () {
  	console.log(this.props.posts)
    return (
      <div>
        <h3>Posts</h3>
        <ul>
          {this.renderPosts()}
        </ul>
        <button onClick={this.nextPage()}>Next</button>

      </div>
    )
  }
}

function mapStateToProps (state) {
  return { posts: state.posts }
}

export default connect(mapStateToProps, { fetchposts })(PostList)
