import axios from 'axios'

export const LOGIN = 'login'
export const SIGNUP = 'signup'

export function login (values, callback) {
  const request = axios.post('http://localhost:8000/token-auth/', values)
  	.then((response) => {
  		if (!response) {
  			return {
          type: LOGIN,
          payload: request
        }
  		}
  		return {
  			type: LOGIN,
  			payload: response
  		}
  	})
    .then(() => callback())
    .catch((error) => {
    	console.log(error.response.data)
    })
  console.log(request)
}

export const signup = (values, callback) => {
  const request = axios.post('http://localhost:8000/api/sign-user')
  console.log(request)
  return {
    type: SIGNUP,
    payload: request
  }
}
