import React from 'react'
import _ from 'lodash'
const PostRender = ({ post, likes }) => {
  const { posts: { results }, update, history } = post
  console.log(update)
  const values = {}
  console.log(results)

  const onSubmit = (event, values) => {
    console.log(values)
    e.preventDefault()
  }

  const indi = () => {
    return _.map(results, post => {
      return (
        <div key={post.pk}>
          <li className='list-group-item rounded-0' onClick={() => { history.push(`/posts/${post.pk}`) }} key={post.pk}>
            {post.name}
            <span className='badge badge-secondary float-right'>Like</span>

          </li>
          <form onSubmit={onSubmit(values)}>
            <span className='badge text-right badge-secondary ml-3' onClick={() => {
              update(post.pk, values)
            }}>
              <button className='btn btn-info' type='submit'>
                { likes }
              </button></span>
          </form>
        </div>
      )
    })
  }
  console.log(indi())
  return (
    <div className='row justify-content-center'>
      <ul className='col-md-8 list-group text-center px-3'>{indi()}</ul>
    </div>
  )
}

// is localStorage in react

export default PostRender
