import _ from 'lodash'
import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { fetchposts, update } from '../actions/notes'
import PostRender from './PostRender'
import PropTypes from 'prop-types'
import { Field, reduxForm } from 'redux-form'
import logo from '../logo.svg'

class PostList extends Component {
  constructor (props) {
    super(props)
    this.state = {
      likes: 15
    }
  }

  componentDidMount () {
    this.props.fetchposts()
  }

  navigateright () {
    this.props.fetchposts(this.props.posts.next)
  }
  navigateleft () {
    this.props.fetchposts(this.props.posts.previous)
  }

  render () {
    console.log(this.props)
    const { posts } = this.props
    const { likes } = this.state
    if (!posts) {
      return (<h1>Loading.......</h1>)
    }

    return (
      <div className='container text-center'>
        <h3 className='mt-4'>Posts</h3>
        <PostRender post={this.props} likes={this.state.likes} />
        { posts.previous !== null
          ? <button
            className='btn btn-default mx-2 my-4 rounded-0'
            onClick={
              () => {
                this.props.fetchposts(this.props.posts.previous)
              }}>
              Previous
          </button>
          : ''}
        { posts.next !== null
          ? <button
            className='btn btn-primary mx-2 my-4'
            onClick={
              () => {
                this.props.fetchposts(this.props.posts.next)
              }}>
              Next
          </button>
          : ''}

        <Link className='btn btn-info rounded-0' to='/posts/new'>Create Post</Link>
        <button className='btn btn-info' onClick={() => {
          this.setState({ likes: likes + 1 })
        }}>Change Likes</button>
        <p>{likes}</p>
        <button className='btn btn-primary' onClick={this.props.fetchposts(this.props.posts.next)}>
          Hello
        </button>
      </div>
    )
  }
}

PostList.propTypes = {
  posts: PropTypes.object.isRequired
}

function mapStateToProps ({ posts }) {
  return { posts: posts }
}

export default connect(mapStateToProps, { fetchposts, update })(PostList)
