import _ from 'lodash'
import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { fetchposts, getCustomersByUrl } from '../actions/notes'

class PostList extends Component {
  componentDidMount () {
    this.props.fetchposts()
  }

  nextPage () {
    this.props.getCustomersByUrl(this.props.posts.next)
  }

  previousPage () {
    this.props.getCustomersByUrl(this.props.posts.previous)
  }
  showdetail (post) {
    return (<div>{post.description}</div>)
  }
  renderPosts () {
    console.log(this.props.posts.count)
    return _.map(this.props.posts.results, post => {
      return (
        <div>
          <li style='{color: red;}' key={post.pk} onClick={this.showdetail.bind(this)}>
            {post.name}
          </li>

        </div>
      )
    })
  }

  render () {
    console.log(this.props.posts)
    return (
      <div>
        <h3>Posts</h3>
        <ul>
          {this.renderPosts()}
        </ul>
        { this.props.posts.previous !== null ? <button onClick={this.previousPage.bind(this)}>Previous</button>
          : ''}
        { this.props.posts.next !== null ? <button onClick={this.nextPage.bind(this)}>Next</button>
          : ''}

      </div>
    )
  }
}

function mapStateToProps (state) {
  return { posts: state.posts }
}

export default connect(mapStateToProps, { fetchposts, getCustomersByUrl })(PostList)
