import React from 'react'
import _ from 'lodash'
import logo from '../img/puss.jpg'
import { Field, reduxForm } from 'redux-form'
import { connect } from 'react-redux'
import { update } from '../actions/notes'

const PostRender = ({ post, likes }) => {
  const { posts: { results }, update, history, handleSubmit } = post
  console.log(post)

  console.log(update)
  console.log(handleSubmit)
  const values = { users: ['hello', 'hi'] }
  console.log(results)

  const onSubmit = (values, pk) => {
    update(values, pk)
    console.log(values)
  }

  const renderField = (field) => {
    console.log(field)
    // field.meta.error is an automatic property added to field object
    // from validate function
    return (
      <div>
        <label>{field.label}</label>
        <input
          className='form-control rounded-0'
          type={`${field.type}`}
          {...field.input}
        />
      </div>
    )
  }

  const indi = () => {
    return _.map(results, post => {
      return (
        <div className='col-md-4 py-2 col-lg-4' key={post.pk}>
          <div className='ledum' onClick={() => { history.push(`/posts/${post.pk}`) }}>
            <img className='h-80 w-100' src={logo} />
          </div>
          <form>
            <Field
              label='Name'
              name='name'
              type='text'
              placeholder='Name'
              component={renderField}
            />
            <div className='my-2'>
              {post.name}
              <button className='btn btn-danger text-right' type='submit'>
                <span
                  className='badge badge-secondary float-right'
                >Like</span>
              </button>

            </div>
          </form>
        </div>
      )
    })
  }
  console.log(indi())
  return (
    <div className='row'>
      {indi()}
    </div>
  )
}

function validate (values) {
  // inorder to validate input return a object we create from validate
  // function....
  const errors = {}

  // if (!values.agree || values.agree == false) {
  //  errors.agree = 'Hello'
  // }

  // if errors is empty the form is ready to submit,
  // else there is a problem and redux form displays errors object
  return errors
}

// is localStorage in react

export default PostRender
