import { LOGIN, LOGOUT, SIGNUP, CHECK, LOGIN_ERROR, AUTH_ERROR, LOGIN_SUCCESSFUL } from '../actions/auth'
const initial = {
  token: localStorage.getItem('token'),
  isAuthenticated: null,
  isLoading: true,
  user: null,
  errors: {}
}

export default function (state = initial, action) {
  switch (action.type) {
  	case LOGIN:
  		localStorage.setItem('token', action.payload.token)
  		state.user = action.payload.user.username
  		console.log(state)
  		console.log(action.payload)
  		return { ...state, isAuthenticated: true, isLoading: false, ...action.payload }

  	case LOGIN_ERROR:
  		console.log(action.payload)
  		return { ...state, isAuthenticated: false, errors: action.payload }

    case CHECK:
    	if (action.token == state.token) {
    		const { payload: { data: { username } } } = action
    		console.log(username)
    		return { ...state, isAuthenticated: true, user: username, isLoading: false }
    	} else {
    		return AUTH_ERROR
    	}

    case AUTH_ERROR:
      console.log(action.payload)
    	return { ...state, isAuthenticated: null, isLoading: false, user: false, errors: action.payload }

    case LOGOUT:
    	if (state.token) {
    		localStorage.removeItem('token')
    	return { ...state, token: null, user: null, isAuthenticated: null }
      }

    default:
      return state
  }
}

