// import _ from 'lodash'
import { ADD_CART } from '../actions/cart'
export let initial

export default function (state = initial, action) {
  switch (action.type) {
    case ADD_CART:
      console.log({ [action.id]: action.colors })
      initial = { [action.id]: action.colors }
      return { [action.id]: action.colors }

    default:
      return state
  }
}
