import _ from 'lodash'
import React, { Component } from 'react'
import { connect } from 'react-redux'
import { fetchpost, deletePost, fetchposts, togg } from '../actions/notes'
import { add_to_cart } from '../actions/cart'
import { Link } from 'react-router-dom'
import logo from '../woah.svg'
import detail from '../img/kris.jpg'

class PostShow extends Component {
  constructor (props) {
    super(props)
    this.state = {
      car: {
        quantity: ''
      },
      quantity: 5,
      id: 1,
      colors: {
        name: 'hello',
        code: 'VVBGFD'
      }
    }
    this.add = this.add.bind(this)
  }

  componentDidMount () {
    const { pk } = this.props.match.params
    this.props.fetchpost(pk)
  }

  onDeleteClick () {
    const { pk } = this.props.match.params
    const { history } = this.props
    this.props.deletePost(pk, () => {
      history.push('/posts')
    })
  }

  add (quantity, id, colors) {
    this.props.add_to_cart(quantity, id, colors)
    const { cart } = this.props
    this.setState({
      quantity: 13,
      id: 5,
      colors: {
        name: 'get out', code: 'WERTER'
      } })
  }

  render () {
    console.log(this.props)
    const { cart, auth } = this.props
    const { post, history } = this.props
    console.log(post)
    const values = {
      'name': 'Hello',
      'heading': 'In it',
      'description': 'Ilove you still',
      'users_like': ['hello']
    }
    const { pk } = this.props.match.params
    const { id, colors, quantity } = this.state
    if (!post) {
      return (<h3>Loading....</h3>)
    } else {
      const check = _.map(post.users_like, (hey) => {
        return hey.username
      })

      console.log(check == 'rick')

      return (
        <div className='container justify-content-center py-3 mt-4'>
          <img className='edit w-100 mb-4' src={detail} />
          <div className='row justify-content-center'>
            <div className='w-100 text-center'>
              <button
                className='btn btn-danger mx-2 text-left rounded-0'
                onClick={this.onDeleteClick.bind(this)}>
        Delete Post
              </button>
              <button
                className='btn btn-info mx-2 rounded-0 text-right'
                onClick={() => { history.push(`/posts/edit/${pk}`) }}>
        Edit Post
              </button>
            </div>

            <p className='text-center mt-3'>
            In a world where stuffs might exist or not all I do is wonder howI normally
            get to where I get to...., nobody there to show me the way and teach me stuffs
            ,in all I do I would always acknowledge the fact that there is a God
              in the universe and my brain won't ever stop thinking about that fact
              , thanks to what I am able to achieve, I keep on giving all my work to the glory of history name,
              I am so glad that some shit worked and some shit did not work thanks to all this am
               now happy about it all, thanks to everyone for showing us the way
               , am so grateful...

            </p>
            <Link className='btn btn-success my-3' to='/posts'>Home</Link>
            <button
              className='btn btn-primary mx-3 my-3'
              onClick={() => { this.props.togg(pk, values) }}>
          Add
            </button>
            {check == 'rick' ? <button className='btn btn-warning'>
            UnLike
            </button>
              : <button className='btn btn-info'>Like</button>}
          </div>
        </div>
      )
    }
  }
}

// ownprops

function mapStateToProps ({ posts, cart, auth }, ownProps) {
  console.log(posts)
  return { post: posts[ownProps.match.params.pk], auth, cart: cart[1] }
}
export default connect(mapStateToProps, { add_to_cart, fetchpost, fetchposts, deletePost, togg })(PostShow)
