import React from 'react'
import _ from 'lodash'
import logo from '../img/puss.jpg'
import { Field, reduxForm } from 'redux-form'
import { connect } from 'react-redux'
import { update } from '../actions/notes'

const PostRender = ({ post, likes }) => {
  const { posts: { results }, update, history } = post
  console.log(update)
  const values = {}
  console.log(results)
  const indi = () => {
    return _.map(results, post => {
      return (
        <div className='col-md-4 py-2 col-lg-4' key={post.pk}>
          <div className='ledum' onClick={() => { history.push(`/posts/${post.pk}`) }}>
            <img className='h-80 w-100' src={logo} />
          </div>
          <div className='my-2'>
            {post.name}
            <span className='badge badge-secondary float-right' onClick={() => { alert('Clicked') }}>Like</span>
          </div>
        </div>
      )
    })
  }
  console.log(indi())
  return (
    <div className='row'>
      {indi()}
    </div>
  )
}

function validate (values) {
  // inorder to validate input return a object we create from validate
  // function....
  const errors = {}

  // if (!values.agree || values.agree == false) {
  //  errors.agree = 'Hello'
  // }

  // if errors is empty the form is ready to submit,
  // else there is a problem and redux form displays errors object
  return errors
}

// is localStorage in react

export default reduxForm({
  validate, // converted to ES6 syntax (validate: validate)
  form: 'PostUser'
})(connect(null, { update })(PostRender))
