import axios from 'axios'

export const LOGIN = 'login'
export const SIGNUP = 'signup'
export const CHECK = 'check'

export function login (values) {
  const request = axios.post('http://localhost:8000/token-auth/', values)
  return (dispatch, getState) => {
  		request.then((response) => {
  			console.log(response.data)
  		dispatch({ type: LOGIN, payload: response.data })
  	}).catch((error) => {
  		console.log(error.response.data)
  	})
  }
}

export const signup = (values, callback) => {
  const request = axios.post('http://localhost:8000/api/sign-user')
  	.then(() => callback())
  console.log(request)
  return {
    type: SIGNUP,
    payload: request
  }
}

export function check_auth () {
  const request = axios.post('http://localhost:8000/token-auth')

  return {
  	type: CHECK,
  	payload: request
  }
}
