import React, { Component } from 'react'
import { connect } from 'react-redux'
import { fetchpost, deletePost } from '../actions/notes'
import { add_to_cart } from '../actions/cart'
import { Link } from 'react-router-dom'
import logo from '../woah.svg'

class PostShow extends Component {
  constructor (props) {
    super(props)
    this.state = {
      car: {
        quantity: ''
      },
      quantity: 5,
      id: 1,
      colors: {
        name: 'hello',
        code: 'VVBGFD'
      }
    }
    this.add = this.add.bind(this)
  }

  componentDidMount () {
    const { pk } = this.props.match.params
    this.props.fetchpost(pk)
  }

  onDeleteClick () {
    const { pk } = this.props.match.params
    const { history } = this.props
    this.props.deletePost(pk, () => {
      history.push('/posts')
    })
  }

  add (quantity, id, colors) {
    this.props.add_to_cart(quantity, id, colors)
    const { cart } = this.props
    this.setState({
      quantity: 13,
      id: 5,
      colors: {
        name: 'get out', code: 'WERTER'
      } })
  }

  render () {
    console.log(this.props)
    const { cart } = this.props
    const { post, history } = this.props
    console.log(post)
    const { pk } = this.props.match.params
    const { id, colors, quantity } = this.state
    if (!post) {
      return (<h3>Loading....</h3>)
    }

    return (
      <div className='container  justify-content-center py-3 mt-4'>
        <img className='edit w-100' src={logo} />
        <button
          className='btn btn-danger mx-2 text-left rounded-0'
          onClick={this.onDeleteClick.bind(this)}>
        Delete Post
        </button>
        <button
          className='btn btn-info mx-2 rounded-0 text-right'
          onClick={() => { history.push(`/posts/edit/${pk}`) }}>
        Edit Post
        </button>
        <h3>{post.name}</h3>
        <p>{post.description}</p>
        <h1>{id}</h1>
        <p>{quantity}</p>
        <p>{colors.name} // {colors.code}</p>
        <Link className='btn btn-success my-3' to='/posts'>Home</Link>
        <button
          className='btn btn-primary mx-3 my-3'
          onClick={() => { this.add(quantity, id, colors) }}>
          Add
        </button>
      </div>
    )
  }
}

// ownprops

function mapStateToProps ({ posts, cart }, ownProps) {
  console.log(ownProps)
  return { post: posts[ownProps.match.params.pk], cart: cart[1] }
}
export default connect(mapStateToProps, { add_to_cart, fetchpost, deletePost })(PostShow)
